import React, { useEffect } from "react";
import "./Loader.css";

const Loader = () => {
  useEffect(() => {
    const expandElements = (elementIds, classNames, delay) => {
      elementIds.forEach((id, index) => {
        setTimeout(() => {
          const element = document.getElementById(id);
          if (element) {
            element.classList.add(classNames[index]);
          }
        }, delay[index]);
      });
    };

    const expandP = ["P1", "P2", "P3", "P4"];
    const expandI = ["I1"];
    const expandX = ["X1", "X2"];
    const expandE = ["E1", "E2", "E3", "E4"];
    const expandL = ["L1", "L2"];
    const delayP = [2000, 2500, 3000, 3500];
    const delayI = [2000];
    const delayX = [2000, 3000];
    const delayE = [2000, 3500, 3000, 2500];
    const delayL = [2000, 3000];

    expandElements(expandP, ["expandp1", "expandp2", "expandp3", "expandp4"], delayP);
    expandElements(expandI, ["expandi1"], delayI);
    expandElements(expandX, ["expandx1", "expandx2"], delayX);
    expandElements(expandE, ["expande1", "expande2", "expande3", "expande4"], delayE);
    expandElements(expandL, ["expandl1", "expandl2"], delayL);
  }, []);

  return (
    <div className="loader">
      <div className="load">
        <div className="load-whole">
          <div className="p">
            <div id="P1" className="p1 a1"></div>
            <div id="P2" className="p2 a1"></div>
            <div id="P3" className="p3 a1"></div>
            <div id="P4" className="p4 a1"></div>
          </div>
          <div className="i">
            <div id="I1" className="i1 a1"></div>
          </div>
          <div className="x">
            <div id="X1" className="x1 a1"></div>
            <div id="X2" className="x2 a1"></div>
          </div>
          <div className="e">
            <div id="E1" className="e1 a1"></div>
            <div id="E2" className="e2 a1"></div>
            <div id="E3" className="e3 a1"></div>
            <div id="E4" className="e4 a1"></div>
          </div>
          <div className="l">
            <div id="L1" className="l1 a1"></div>
            <div id="L2" className="l2 a1"></div>
            <div className="l3 l3-1"></div>
            <div className="l3 l3-2"></div>
            <div className="l3 l3-3"></div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Loader;
