import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { backendUrl } from "../exports";
import Navbar from "../Components/Navbar";

const Results = () => {
  const { searchTerm } = useParams();
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchResults = async () => {
      try {
        const response = await fetch(
          `${backendUrl}suggestions/search?term=${searchTerm}`
        );
        const data = await response.json();
        setResults(data.results);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching search results:", error);
        setLoading(false);
      }
    };
    fetchResults();
  }, [searchTerm]);

  const handleSwipe = async (receiverId, action) => {
    try {
      const response = await fetch(
        `${backendUrl}matching/swipe?userId=${localStorage.getItem(
          "userId"
        )}`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ receiverId, action }),
        }
      );

      if (response.ok) {
        const updatedResults = results.filter((user) => user.id !== receiverId);
        setResults(updatedResults);
      } else {
        console.error("Swipe action failed:", response.statusText);
      }
    } catch (error) {
      console.error("Swipe action error:", error);
    }
  };

  const fetchUserProfile = async (userId) => {
    try {
      const response = await fetch(
        `${backendUrl}userprofile/profile?userId=${userId}`
      );
      const data = await response.json();
      return data.userProfile; // Return user profile data
    } catch (error) {
      console.error("Error fetching user profile:", error);
      return null;
    }
  };

  return (
    <>
      <Navbar />
      <div className="results-page">
        <h2>Search Results for "{searchTerm}"</h2>
        {loading ? (
          <p>Loading...</p>
        ) : results.length === 0 ? (
          <p>No results found.</p>
        ) : (
          <div className="results-container">
            {results.map(async (result) => {
              const userProfile = await fetchUserProfile(result.userid);
              if (userProfile) {
                return (
                  <div key={result.id} className="result-item">
                    <h3>{result.user_name}</h3>
                    <img src={userProfile.image_url} alt={userProfile.name} className="user-image" />
                    <p>{result.bio}</p>
                    <p>Age: {result.age}</p>
                    <p>Gender: {result.gender}</p>
                    <p>Location: {result.location}</p>
                    <p>Interests: {result.interests.join(", ")}</p>
                    <div className="btns">
                      <button className='like-btn' onClick={() => handleSwipe(result.userid, "like")}>
                        Like
                      </button>
                      <button className='dislike-btn' onClick={() => handleSwipe(result.userid, "dislike")}>
                        Dislike
                      </button>
                    </div>
                  </div>
                );
              }
              return null; // Return null if user profile data is not available
            })}
          </div>
        )}
      </div>
    </>
  );
};

export default Results;
