import React, { useState, useEffect } from "react";
import { backendUrl } from "../exports";
import Navbar from "../Components/Navbar";

const Notif = () => {
  const [likedUsers, setLikedUsers] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchLikedUsers = async () => {
      try {
        const response = await fetch(
          `${backendUrl}matching/notifications?userId=${localStorage.getItem(
            "userId"
          )}`
        );
        const data = await response.json();
        setLikedUsers(data.likedUsers);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching liked users:", error);
        setLoading(false);
      }
    };

    fetchLikedUsers();
  }, []);

  const fetchUserProfile = async (userId) => {
    try {
      const response = await fetch(
        `${backendUrl}userprofile/profile?userId=${userId}`
      );
      const data = await response.json();
      return data.userProfile; // Return user profile data
    } catch (error) {
      console.error("Error fetching user profile:", error);
      return null;
    }
  };

  return (
    <>
      <Navbar />
      <div className="notif-page">
        <h2>Notifications</h2>
        {loading ? (
          <p>Loading...</p>
        ) : likedUsers.length === 0 ? (
          <p>No notifications found.</p>
        ) : (
          <ul>
            {likedUsers.map(async (userId) => {
              const userProfile = await fetchUserProfile(userId);
              if (userProfile) {
                return (
                  <li key={userId}>
                    <img src={userProfile.image_url} alt={userProfile.name} className="user-image" />
                    {userProfile.name} liked you
                  </li>
                );
              }
              return null; // Return null if user profile data is not available
            })}
          </ul>
        )}
      </div>
    </>
  );
};

export default Notif;
