import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom"; // Import useNavigate from react-router-dom
import Loader from "./Loader"; // Import your loader component

const LoaderAnimation = () => {
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate(); // Use useNavigate hook

  useEffect(() => {
    // Simulate loading delay (3 seconds for example)
    const timer = setTimeout(() => {
      setLoading(false);
    }, 4000);

    return () => clearTimeout(timer); // Cleanup the timer on unmount
  }, []);

  useEffect(() => {
    // Redirect to the main page once animation is complete
    if (!loading) {
      const redirectTimer = setTimeout(() => {
        navigate("/home"); // Replace "/home" with the path to your main page
      }, 1000); // Adjust the delay to match the duration of your animation

      return () => clearTimeout(redirectTimer); // Cleanup the timer on unmount
    }
  }, [loading, navigate]);

  return (
    // Render loader animation while loading
    loading && <Loader />
  );
};

export default LoaderAnimation;
