import React, { useEffect } from 'react';
import { gsap } from 'gsap';
import Splitting from 'splitting';


// CSS-in-JS styling or import CSS file
const styles = {
  root: {
    margin: 0,
    padding: 0,
    boxSizing: 'border-box',
    fontFamily: 'Wix Madefor Display, sans-serif',
    display: 'grid',
    placeContent: 'center',
    minHeight: '100vh',
    background: '#111',
  },
  heading: {
    color: '#fff',
    fontSize: '6em',
    textTransform: 'uppercase',
    lineHeight: 1,
    fontWeight: 800,
    letterSpacing: '-4px',
  },
};

const Loader = () => {
  useEffect(() => {
    // Initialize splitting
    Splitting();

    // GSAP animation
    gsap.from('.char', {
      rotateY: 180,
      opacity: 0,
      stagger: {
        amount: 1,
      },
      duration: 1,
      ease: 'power3.Out',
    });
  }, []);

  return (
    <div style={styles.root}>
      <h1 style={styles.heading} data-splitting>
        Circuit
      </h1>
    </div>
  );
};

export default Loader;
