import React, { useEffect, useState } from "react";
import { useHistory } from "react-router-dom";
import Loader from "./Loader"; // Import your loader component

const LoaderAnimation = () => {
  const [loading, setLoading] = useState(true);
  const history = useHistory();

  useEffect(() => {
    // Simulate loading delay (3 seconds for example)
    setTimeout(() => {
      setLoading(false);
      // Redirect to the main page once animation is complete
      history.push("/home"); // Replace "/home" with the path to your main page
    }, 3000);
  }, [history]);

  return (
    // Render loader animation while loading
    loading && <Loader />
  );
};

export default LoaderAnimation;
